// request <> response
import auth0 from '../../lib/auth0'

const login = async (request, response) => {
    await auth0.handleLogin(request, response)
    response.send({
        name: 'Jhonatan de Oliveira'
    })
}
// autenticação: multi factor auth
// autorização
export default login
